import React, { useState } from "react";
import "./Grid.css";

const Grid = ({ numRows, numsCols }) => {
  const initialGrid = () => {
    const grid = [];
    for (let i = 0; i < numRows; i++) {
      const row = [];
      for (let j = 0; j < numCols; j++) {
        row.push(Math.random() < 0.05 ? 1 : 0);
      }
      grid.push(row);
    }
    return grid;
  };

  const renderGrid = () => {
    const grid = initialGrid();
    return (
      <div className="grid">
        {grid.map((row, i) => (
          <div key={i} className="row">
            {row.map((cell, j) => (
              <div
                key={`${i}-${j}`}
                className={`cell ${cell ? "alive" : "dead"}`}
              />
            ))}
          </div>
        ))}
      </div>
    );
  };

  return renderGrid();
};

export default Grid;
