import React, { useState, useEffect } from "react";
import Grid from "./Grid";
import "./GameOfLife.css";

const GameOfLife = () => {
  const [numRows, setNumRows] = useState(20);
  const [numCols, setNumCols] = useState(20);
  const [grid, setGrid] = useState([]);
  const [livingCells, setLivingCells] = useState(0);
  const [intervalId, setIntervalId] = useState(null);
  const [isRunning, setIsRunning] = useState(false);

  useEffect(() => {
    initializeGrid();
  }, [numRows, numCols]);

  useEffect(() => {
    countLivingCells();
  }, [grid]);

  const initializeGrid = () => {
    const newGrid = Array.from({ length: numRows }, () =>
      Array.from({ length: numCols }, () => (Math.random() < 0.05 ? 1 : 0))
    );
    setGrid(newGrid);
  };

  const toggleCell = (i, j) => {
    const newGrid = [...grid];
    newGrid[i][j] = newGrid[i][j] ? 0 : 1;
    setGrid(newGrid);
  };

  const countLivingCells = () => {
    let count = 0;
    grid.forEach((row) => {
      row.forEach((cell) => {
        if (cell) count++;
      });
    });
    setLivingCells(count);
  };

  const stepSimulation = () => {
    const newGrid = Array.from({ length: numRows }, (_, i) =>
      Array.from({ length: numCols }, (_, j) => applyRules(i, j))
    );
    setGrid(newGrid);
  };

  const applyRules = (i, j) => {
    const neighbors = [
      [i - 1, j - 1],
      [i - 1, j],
      [i - 1, j + 1],
      [i, j - 1],
      [i, j + 1],
      [i + 1, j - 1],
      [i + 1, j],
      [i + 1, j + 1],
    ];
    let liveNeighbors = 0;
    neighbors.forEach(([x, y]) => {
      if (x >= 0 && x < numRows && y >= 0 && y < numCols && grid[x][y]) {
        liveNeighbors++;
      }
    });
    if (grid[i][j]) {
      if (liveNeighbors < 2 || liveNeighbors > 3) return 0;
      return 1;
    } else {
      if (liveNeighbors === 3) return 1;
      return 0;
    }
  };

  const startSimulation = () => {
    const id = setInterval(() => {
      stepSimulation();
    }, 500); // adjust speed here
    setIntervalId(id);
    setIsRunning(true);
  };

  const stopSimulation = () => {
    clearInterval(intervalId);
    setIntervalId(null);
    setIsRunning(false);
  };

  const resetGrid = () => {
    clearInterval(intervalId);
    setIntervalId(null);
    setIsRunning(false);
    initializeGrid();
  };

  return (
    <div className="game-container">
      <div className="grid-info">
        <span>Living Cells: {livingCells}</span>
        <button onClick={resetGrid}>Reset Grid</button>
        <button onClick={isRunning ? stopSimulation : startSimulation}>
          {isRunning ? "Stop Simulation" : "Start Simulation"}
        </button>
      </div>
      <Grid grid={grid} toggleCell={toggleCell} />
    </div>
  );
};

export default GameOfLife;

