import React from "react";
import "./Grid.css";

const Grid = ({ grid, toggleCell, cellChangeCount }) => {
  return (
    <div className="grid">
      {grid.length > 0 &&
        grid.map((row, i) => (
          <div key={i} className="row">
            {row.map((cell, j) => (
              <div
                key={`${i}-${j}`}
                className={`cell ${cell ? "alive" : "dead"}`}
                onClick={() => toggleCell(i, j)}
                style={{
                  backgroundColor: cell
                    ? `rgba(0, 255, 0, ${Math.min(
                        1,
                        cellChangeCount[i][j] / 10
                      )})`
                    : `rgba(255, 255, 255, ${Math.min(
                        1,
                        cellChangeCount[i][j] / 10
                      )})`,
                }}
              />
            ))}
          </div>
        ))}
    </div>
  );
};

export default Grid;

