import React from "react";
import "./Grid.css";

const Grid = ({ grid, heatMap, toggleCell }) => {
  return (
    <div className="grid">
      {grid.map((row, i) => (
        <div key={i} className="row">
          {row.map((cell, j) => (
            <div
              key={`${i}-${j}`}
              className={`cell ${cell ? "alive" : "dead"}`}
              style={{
                backgroundColor:
                  heatMap[i][j] === 0
                    ? "#ffffff"
                    : `hsl(120, 100%, ${10 * heatMap[i][j]}%)`,
              }}
              onClick={() => toggleCell(i, j)}
            />
          ))}
        </div>
      ))}
    </div>
  );
};

export default Grid;

