import React, { useState } from "react";
import Grid from "./Grid";

const GameOfLife = () => {
  const [numRows, setNumRows] = useState(20);
  const [numCols, setNumCols] = useState(20);
  const [heightError, setHeightError] = useState("");
  const [widthError, setWidthError] = useState("");

  function initialGrid() {
    // const grid = [];
    // for (let i = 0; i < numRows; i++) {
    //   const row = [];
    //   for (let j = 0; j < numsCols; j++) {
    //     row.push(Math.random() < 0.05 ? 1 : 0);
    //   }
    //   grid.push(row);
    // }
    return <Grid />;
  }

  function ChangeHeight(e) {
    const newHeight = parseInt(e.target.value);
    if (newHeight >= 3 && newHeight <= 40) {
      setNumRows(newHeight);
      setHeightError("");
    } else {
      setHeightError("row number must between 3 and 40");
    }
  }

  function ChangeWidth(e) {
    const newWidth = parseInt(e.target.value);
    if (newWidth >= 3 && newWidth <= 40) {
      setNumCols(newWidth);
      setWidthError("");
    } else {
      setWidthError("col number must between 3 and 40");
    }
  }

  return (
    <div className="center">
      <div>
        <input type="number" value={numRows} onChange={ChangeHeight} />
        {heightError && <div className="error">{heightError}</div>}
        <input type="number" value={numCols} onChange={ChangeWidth} />
        {widthError && <div className="error">{widthError}</div>}

        <button onClick={initialGrid}>Reset</button>

        <Grid numRows={numRows} numCols={numCols} />
      </div>
    </div>
  );
};

export default GameOfLife;
