import React, { useState, useEffect } from "react";
import Grid from "./Grid";
import "./GameOfLife.css";

const GameOfLife = () => {
  const [tempNumRows, setTempNumRows] = useState(20);
  const [tempNumCols, setTempNumCols] = useState(20);
  const [numRows, setNumRows] = useState(20);
  const [numCols, setNumCols] = useState(20);
  const [heightError, setHeightError] = useState("");
  const [widthError, setWidthError] = useState("");
  const [grid, setGrid] = useState(initialGrid());

  function initialGrid() {
    const grid = [];
    for (let i = 0; i < numRows; i++) {
      const row = [];
      for (let j = 0; j < numCols; j++) {
        row.push(Math.random() < 0.05 ? 1 : 0);
      }
      grid.push(row);
    }
    return grid;
  }

  useEffect(() => {
    setGrid(initialGrid());
  }, [tempNumRows, tempNumCols]);

  function ChangeHeight(e) {
    const newHeight = parseInt(e.target.value);
    setTempNumRows(newHeight);
  }

  function ChangeWidth(e) {
    const newWidth = parseInt(e.target.value);
    setTempNumCols(newWidth);
  }

  const handleKeyDown = (e) => {
    if (e.keyCode === 13) {
      if (tempNumRows < 3 || tempNumRows > 40) {
        setHeightError("Please enter a number between 3 and 40");
      } else if (tempNumCols < 3 || tempNumCols > 40) {
        setWidthError("Please enter a number between 3 and 40");
      } else {
        setNumRows(tempNumRows);
        setNumCols(tempNumCols);
        setHeightError("");
        setWidthError("");
      }
    }
  };

  function reset() {
    setNumCols(20);
    setNumRows(20);
    setTempNumCols(20);
    setTempNumRows(20);

    setGrid(initialGrid());
    setHeightError("");
    setWidthError("");
  }

  return (
    <div className="center-container">
      <div>
        <input
          type="number"
          value={tempNumRows}
          onChange={ChangeHeight}
          onKeyDown={handleKeyDown}
        />
        {heightError && <div className="error">{heightError}</div>}

        <input
          type="number"
          value={tempNumCols}
          onChange={ChangeWidth}
          onKeyDown={handleKeyDown}
        />
        {widthError && <div className="error">{widthError}</div>}

        <button onClick={reset}>Reset</button>

        <Grid numRows={numRows} numCols={numCols} />
      </div>
    </div>
  );
};

export default GameOfLife;
