import React, { useState } from "react";
import "./Grid.css";

const [grid, setGrid] = useState(initialGrid());

const initialGrid = ({ numRows, numsCols }) => {
  const grid = [];
  for (let i = 0; i < numRows; i++) {
    const row = [];
    for (let j = 0; j < numsCols; j++) {
      row.push(Math.random() < 0.05 ? 1 : 0);
    }
    grid.push(row);
  }
  return grid;
};

export default initialGrid;
